mapper.update:
update t_dish
set monthSales = monthSales + #{num}
where id = #{id}

mapper.select:
select *
from t_dish
where name = #{name} and storeId = #{storeId}

select d.id as id, d.name as name, d.typeId as typeId, dt.name as typeName, d.price as price, d.monthSales as monthSales, d.detail as detail, d.storeId as storeId from t_dish d left join t_dish_type dt on (d.typeId = dt.id) where 1=1
and d.storeId =#{queryStoreId}
and d.typeId = #{queryTypeId}
and d.name like concat('%',#{queryName},'%')
