mapper.update:
update t_store
set monthSell = #{monthSell},
	avgSendTime = #{avgSendTime}
where id = #{id}

update t_store
set openOrClose = #{openOrClose}
where id = #{id}

mapper.select:
select *
from t_store
where 1=1

	and openOrClose = #{queryOpenOrClose}


	and name = #{queryName}
